import React, { useState } from 'react';
import AddIcon from '@material-ui/icons/Add';
import Button from '@material-ui/core/Button';

const Text_box = ()=>{

    const [current_text,update_text] = useState({text:''});
    const [current_list,update_list] = useState({title:''})

    const title = (e)=>{
        update_text(()=>{
                return{ text:e.target.value}
        }    
        )
    }

    const add= ()=>{
        update_list((prev_list)=>{
            return{
                current_list:[...prev_list,current_text.text]
            }
        }); update_text(
            ()=>{
                return{text:''}
            }
        )
        
    }

  return(

    <>
    <div className='text_box'>
        <h2 style={{marginTop:"5%"}}>Your Task</h2>
        <input type='text' value={current_text.text} placeholder='Your Title' onChange={title}/>
        <input type='text' placeholder='Your Task'/>
        <Button  onClick={add}><AddIcon className='but'/></Button>
    </div>
    
    </>
  )
}

export default Text_box
