import React, { useState } from 'react';
import AddIcon from '@material-ui/icons/Add';
import Button from '@material-ui/core/Button';

const Text_box = (props)=>{

    const [current_text,update_text] = useState({title:'',content:''});
    const [show,update_show] = useState(false);

    

    const title = (e)=>{
        const {name,value} = e.target
        update_text((prev_data)=>{
                return{
                    ...prev_data,
                    [name]:value
                 }
        }    
        )
    }

    const add= (e)=>{
       props.send_task(current_text)
       update_text(()=>{
           return{
           title:'',
           content:''}
       })
        
    }

    const display = ()=>{
        update_show(()=>{
            return(true)
        
    })}


  return(

    <>
    <div className='text_box' onClick={display}>
        <h2 style={{marginTop:"5%"}}>Your Task</h2>
        {show?
        <input type='text' name='title' value={current_text.title} placeholder='Your Title' onChange={title}/>:null}
        <input type='text' name = 'content' placeholder='Your Task' value={current_text.content} onChange={title}/>
        <Button  onClick={add}><AddIcon className='but'/></Button>
    </div>
    
    </>
  )
}

export default Text_box;
